INSERT INTO page (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at) VALUES (2, true, 'О компании', '<p>Компания Рекада является официальным дистрибьютером завода КЕРАТОН уже более 10 лет с 1999 года. Кератон производит керамический гранит под торговой маркой Estima. Имея долговременные стратегические отношения с крупнейшим производителем в России, реализуя значительные объемы товара, Рекада может предложить своим клиентам керамогранит Эстима по оптовым ценам или по спецценам для особо крупных объектов.&nbsp;</p>
INSERT INTO page (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at) VALUES (3, true, 'Контакты', '<p><strong>Как добраться пешком от метро:</strong>&nbsp;Станция м. Нагатинская (первый вагон из центра).</p><p>От метро двигайтесь вдоль Варшавского шоссе по ходу движения транспорта 7-8 минут средним темпом (или можно проехать одну остановку на любом транспорте и далее идти вперед еще 1-2 минуты). 6-этажное здание бизнес-центра, расположенно вдоль шоссе (вдоль здания стоят высокие ели, на углу есть адресный указатель - &quot;Варшавское шоссе, д. 42&quot;).</p><p>Вход находится по центру фасада со стороны шоссе (круглые вращающиеся двери). Посетителям необходимо спуститься направо, вниз на цокольный этаж к гостевому ресепшн, назвать свою фамилию и сказать, что пришли в компанию &quot;Рекада&quot;. На лифте подняться на 5-й этаж, из лифтового холла повернуть налево, на двери офиса находится вывеска &quot;Рекада-Центр&quot;.</p>', 'contacts', 'Контакты', NULL, NULL, 0, 'contacts', true, 1, '1.3', 'page', '2013-07-01 11:36:55', '2013-07-01 11:36:55');
