INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (105, true, 'Изготовление ступеней из стандартных плит', '<p>На данном рисунке показана стандартная ступень из керамогранита. Такая ступень изготавливается из обычной плитки, имеющей форматы 300х300, 400х400, 300х600 или 600х600. Насечки на поверхности обеспечивают заметный эффект противоскольжения, а закруглённый на 1/4 круга торец уменьшает риск споткнуться. Компания Рекада имеет значительный складской запас уже готовых ступеней такого рода и готова поставить изделия в кратчайшие сроки.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (114, true, 'Обработка торца под 45''', '<p>Подрезание торцевых частей плит под углом 45 градусов - весьма&nbsp;востребованная работа, когда плитки из керамогранита монтируются по &quot;мокрой&quot; технологии, и должны плотно пристыковываться на углах здания. Для качественной обработки торца керамогранита, гранитных плит, природного или искусственного камня&nbsp;рекомендуется использовать гидрорезку. Для расчёта стоимости работ обратитесь к специалистам в компанию Рекада.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (106, true, 'Изготовление плинтусов из стандартных плит', '<p>Керамогранитный плинтус, показанный на этой странице, изготавливается из любой плитки с размерами 300х300, 400х400, 600х600 или др. Обычно высота плинтуса составяет 90 или 100мм, но может быть любой, на усмотрение заказчика. Верхняя часть плинтуса закругляется для усиления эстетического эффекта. Аккуратное обрамление пола плинтусами &nbsp;является обязательным условием для помещений с высоким уровнем качества ремонта и отделки. Компания Рекада готова предоставить заказчикам в кратчайшие сроки уже готовые плинтуса, находящиеся на складе, или изготовить под заказ в течение 7-14 дней.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (115, true, 'Обработка слебов', '', 'obrabotka-slebov', 'Обработка слебов', '', '', 1200, 'obrabotka-slebov', false, 80, '1.29.80.115', 'product', '2013-08-19 16:05:32', '2013-08-19 16:05:32', 'rezsleb', 1.00, '', NULL, NULL, NULL, false, false, false, NULL, NULL, NULL, '', NULL);
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (125, true, 'Соло 12 мм', '<p>Керамогранит Соло имеет специальную толщину в 12мм и специальный формат - 200*200. Это так называемый технический керамогранит, его назначение - использование в складских, производственных, торговых или подсобных помещениях, где подразумеваются высокие или сверхвысокие нагрузки на поверхность, например с использованием автопогрузчиков и складских стеллажей. 12мм-керамогранит в малом формате имеет&nbsp;высокий ресурс эксплуатации в подобных экстремальных условиях.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (37, true, 'Trend 40*40', '<p>Керамогранит TR - это коллекция, которую пожалуй еще не распробовал покупатель. TREND - керамогранит очень красивый, темные вкрапления разной величины, а также яркие и необычные фоновые цвета подняли его в более высокий класс. Сложность изготовления, яркость цветов оправдывает сравнительно высокую цену на эту коллекцию.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (116, true, 'Заказное панно', '<p>Компания Рекада,&nbsp;согласно технического задания заказчика,&nbsp;выполняет работы&nbsp;по изготовлению&nbsp;панно из керамогранита&nbsp;с любым рисунком, такие как: фирменный логотип, портреты, пейзажи или фантазийные мозаики. Обратитесь к менеджеру проектов для выяснения сроков выполнения и стоимости дизайнерских услуг.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (149, true, 'Технический керамогранит 20*20', '<p>Технический керамогранит предназначен для использования в таких помещениях, где на поверхность может оказываться значительная нагрузка (например: складские помещения, торговые помещения, помещения&nbsp;производственного назначения). Поэтому это, как правило, 12мм-керамогранит, то есть он имеет увеличенную толщину. Техногрес - это всегда мелкоформатный керамогранит 20*20 или керамогранит 30*30.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (38, true, 'Trend 60*60', '<p>Керамогранит TR - это коллекция, которую пожалуй еще не распробовал покупатель. TREND - керамогранит очень красивый, темные вкрапления разной величины, а также яркие и необычные фоновые цвета подняли его в более высокий класс. Сложность изготовления, яркость цветов оправдывает сравнительно высокую цену на эту коллекцию.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (107, true, 'Ступени с носиком', '<p>Сборные керамические ступени являются альтернативой для классических ступеней с насечками и закруглённым торцом. Во-первых, они имеют интересный вид&nbsp;с точки зрения дизайна, имитируя более толстые плиты из натурального камня. Во-вторых, ступени с носиком аккуратно закрывают зону подступёнка. Но с другой стороны,&nbsp;они имеют недостаток в том, что приклееный носик к керамической плитке может отклеиться под влиянием нагрузок, воды, льда и перепадов температуры. Таким образом, клеенные ступени менее&nbsp;надёжны для укладки на внешних пространствах.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (35, true, 'Standart 60*60', '<p>Коллекция керамогранита Estima ST или Standart - одна из самых старых и наиболее известных на рынке. Керамогранит ST имеет простой рисунок - фоновый цвет с мелкой крапинкой, т.н. керамогранит соль-перец. Такой цвет достаточно легок в изготовлении, соответственно - это недорогой материал, его простой цвет универсален в дизайне, и это и есть причина его сверхпопулярности.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (51, true, 'Палаццо 60*60', '<p>Керамогранит Палаццо - исключительный выбор для любителей роскоши.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (36, true, 'Stone 30*30', '<p>Коллекция керамогранита STONE - адаптированный вариант хорошо известного на рынке рельефного керамогранита ESTIMA ANTICA. Он лишен высокого дизайнерского стиля и, таким образом, очень прагматичен. Его назначение - входные группы или любые площади, где требуется т.н. антискользящий керамогранит.</p>
