INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (64, true, 'Dream 60*60', '<p>Керамогранит DREAM - сравнительно новая коллекция. В ней отражается более спокойная цветовая гамма, более легкий изгиб рисунка в сравнении с другими коллекциями ESTIMA из серии керамогранит под мрамор. Керамогранит DR имеет относительно много пастельных оттенков, и имеет хороший спрос у специалистов в области дизайна помещений.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (108, true, 'Составная ступень', '<p>Составные ступени представляют собой единый комплект ступени, бордюра&nbsp;и подступёнка. Длины ступеней и подступенка из керамогранита обычно варьируются от 60 до 120мм. Сборная ступень подразумевает высокий класс отделки лестниц, воплощая единый стиль и монолитность облицовки.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (142, true, 'Технический керамогранит 30*30', '<p>Технический керамогранит предназначен для использования в таких помещениях, где на поверхность может оказываться значительная нагрузка (например: складские помещения, торговые помещения, помещения&nbsp;производственного назначения). Поэтому это, как правило, 12мм-керамогранит, то есть он имеет увеличенную толщину. Техногрес - это всегда мелкоформатный керамогранит 20*20 или керамогранит 30*30.</p>
