INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (82, true, 'Вырез ячейки', '<p>Вырез ячейки в составе нескольких плит - эту работу опять-таки рекомендуется выполнить по технологии гидрорезки, т.к. требуется точность и имеется большой риск боя плиток при выполнении работы ручным инструментом. Ячейка в плитке может быть необходима для вставки декоративного элемента или для расположения в этом месте какого-либо элемента оборудования (электрического, сантехнического и др.).</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (83, true, 'Квадратное отверстие', '<p>Вырез квадратного отверстия в плите невозможно сделать в кустарных условиях ни с помощью плиткореза, ни с помощью болгарки. Это с виду сложная работа легко и просто выполняется с помощью гидрорезки. Квадратное отверстие в плитке необходимо, чтобы вставить определённого вида лючок, декоративную вставку или электрическую розетку. Позвоните в офис компании Рекада для получения расчёта стоимости такой работы.&nbsp;</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (79, true, 'Столешница', '', 'stoleshnitsa-pod-rakovinu', 'Столешница под раковину', '', '', 30, 'stoleshnitsa-s-1-vyrezom', false, 75, '1.29.75.79', 'product', '2013-07-25 17:35:51', '2013-07-25 17:35:51', 'vrst', 95000.00, '9c83d19e27ae60e07b3991ed3fee60e2.png', NULL, NULL, NULL, false, false, false, NULL, NULL, NULL, NULL, NULL);
