INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (109, true, 'Сверление под кайл', '<p>Свереление отверстий в фасадных керамогранитных плитах - необходимость при использовании так называемого скрытого способа крепления облицовочного керамогранита. Такой вариант крепления противопоставлен открытому способу - с помощью кляммеров (кляймеров).&nbsp;В таком случае лапки крепежного элемента захватывают плиту и видны снаружи. Крепление же с помощью кайла (&quot;keil&quot;) позволяет скрепить подконструкцию вентсистемы и керамогранит изнутри. Но для этого, согласно проектной документации, требуется просверлить отверстия с обратной стороны плитки. Кайл, расширяясь внутри отверстия, исполняет таким образом&nbsp;функцию &quot;железного захвата&quot;, происходит надёжный и невозвратный крепёж. Нюанс в том, что сверление отверстия должно быть выполнено очень точно, быть не сквозным, но достаточно глубоким, и, главное, расширяться в глубине материала. Обратитесь к нашим менеджерам для выяснения стоимости указанных работ.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (111, true, 'Торцевые прорези', '<p>Прорези в торцах керамогранитных плит, а также в плитах из гранита, мрамора или агломерата, могут быть необходимы при монтаже навесного вентилируемого фасада с помощью планок или с помощью специальных кляммеров. Такой способ - способ скрытого крепления керамогранита в вентсиситемах&nbsp;- более эстетичен, т.к.&nbsp;внешние стороны облицовочных плит&nbsp;остаются свободными. При открытом же способе крепеления&nbsp;лапки кляммера снаружи&nbsp;огибают и удерживают плиту, но при этом выступающие металлические части крепежа портят&nbsp;красивую монолитную облицовочную поверхность. Изготовление ровных канавок в торцах гранитных плит представляет собой сложную работу: во-первых, возникает риск скола тонких стенок плиток, во-вторых, неоходимо, чтобы&nbsp;канавка&nbsp;была абсолютно ровной и параллельной к краю. Обратитесь к специалистам&nbsp;компании Рекада&nbsp;для расчёта стоимости указанной работы.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (123, true, 'Каскад', '<p>Керамогранит Каскад -&nbsp;удачная тема в стиле керамогранитной мозаики. Классическая мозаика является неотъемлемым материалом в отделке бассейнов, купелей, помещений для душа, сауны, т.к. решает как минимум две задачи. Во-первых, плитка, то намокая, то высыхая, и таким образом - сжимаясь и расширяясь,&nbsp;должна иметь дополнительное пространство для своего хода в виде межплиточных швов. Во-вторых, ее бугристая поверхность имеет отличный противоскользящий эффект, уместный во влажных помещениях. Каскад представлят собой готовую мозаичную пластину, которая смыкаясь с другими плитами образует круги,&nbsp;вид которых украсят любое пространство.&nbsp;</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (146, true, 'Marmi 60*60', '', 'marmi-6060', 'Marmi 60*60', '', '', 190, 'marmi-4040', false, 32, '1.13.32.146', 'product', '2013-08-28 14:14:23', '2013-08-28 14:14:23', 'mr', 680.00, 'marmi.jpg', 2, 3, 1, false, false, false, 60.0, 60.0, 10.0, 'marmi.jpg', 12);
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (91, true, 'Antica 30*30', '<p>Керамогранит ANTICA - это так сказать нишевый материал, он очень специфичен, но у него есть изюминка, которая и позволила ему так долго оставаться популярным. Во-первых, это рельефный керамогранит, т.н. керамогранит под камень или под состаренный камень, он весьма популярен в дизайне. Во-вторых, имея структурированную поверхность, очень востребован для отделки входных групп как противоскользящий керамогранит.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (81, true, 'Прямая резка', '<p>Прямые резы керамогранита - это наиболее простая работа, и зачастую она может быть выполнена заказчиком самостоятельно либо ручным интструментом (плиткорезом), либо стационарной электрической угло-шлифовальной машиной с &quot;алмазным&quot; диском для камня (болгарка). Но когда встаёт вопрос о большом объёме работ, высоком качестве получаемых изделий или особенных свойств плиты (крупноформатные плиты, значительная толщина), - тогда возникает потребность в гидроабразивной резке. Компания Рекада предоставляет большой список работ по прямой резке, причём в каждом изделии будет снята фаска, вплоть до долей милиметра быдут соблюдены параметры по длине, ширине и другим геометрическим показателям.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (84, true, 'Срез углов', '<p>Срез углов керамогранита востребован когда укладка плит происходит не параллельно стенам, а под углом. Ещё одна причина - подготовка больших плит под небольшие квадратные вставки (декоры). Это достаточно масштабная работа, требующая высокоточной резки плит, а значит лучшим способом будет гидроабразивная резка.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (86, true, 'Закругление углов', '<p>Закругление углов плит из керамогранита, гранита или мрамора особенно часто используется при изготовлении из них лестничных ступеней, подоконников или столешниц. Зачастую также эта работа совмещается и с дополнительной обработкой торцов плиток: закругление на 1/4 круга или 1/2 круга. Это объясняется тем, что обрабатываемые плиты, как правило, выступают от основы или находятся на краю рабочей поверхности, и, следовательно, острые края не должны мешать эксплуатации.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (87, true, 'Закругление по дуге', '<p>Закругление керамогранитных плит по дуге часто применяется в следующих случаях: изготовление столешниц &nbsp;из гранита, мрамора или керамогранита (для кухни, ванных комнат, баров, приёмных); изготовления подоконников &nbsp;из камня, гранита, мрамора или керамогранита (для эркеров, обзорных зон помещений); изготовления ступеней лестниц (входные группу в виде полугруга, винтовые лестницы). Также к этим работам, как правило, прибавляются работы по обработке края плит с изготовлением разлчиных его профилей (в 1/2 круга, в 1/4 круга, волнообразные).</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (96, true, 'Standart 40*40', '<p>Коллекция керамогранита Estima ST или Standart - одна из самых старых и наиболее известных на рынке. Керамогранит ST имеет простой рисунок - фоновый цвет с мелкой крапинкой, т.н. керамогранит соль-перец. Такой цвет достаточно легок в изготовлении, соответственно - это недорогой материал, его простой цвет универсален в дизайне, и это и есть причина его сверхпопулярности.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (112, true, 'Ажурная гравировка', '<p>Ажурная гравировка на плитке - необычный способ обработки керамогранита. Узоры, портреты, пейзажи, логотипы или надписи на керамогранитных плитках - это только неполный список уникальных работ, которые выполняются по требованию заказчика.&nbsp; Для определения сроков и расчёта стоимости обратитесь к специалистам компании Рекада.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (85, true, 'Пристыковка плит', '<p>Пристыковка плит керамогранита разного цвета - это модная ныне тенденция в дизайне при напольной укладке природного или искуственного камня. Плавные линии в виде синусоид, гипербол или парабол, которые образуются при соприкосновении контрастных по цвету плит, стабильно производят эффект дороговизны отделки и сложности выполненых работ. Не говоря уже о том, что это действительно красиво. Волнообразная резка керамогранита осуществляется с помощью технологии гидроабразивной резки согласно дизайнерского проекта, который в электронном виде становится программой для станков с ЧПУ. Компания Рекада готова предоставить услуги по полному решению данной задачи - от дизайна до доставки уже готовых изделий, а также укладки керамогранита, гранита или мрамора.</p>
