INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (110, true, 'Сквозные круглые отверстия', '<p>Сквозные круглые отверстия в&nbsp;керамогранитной плитке (в т.ч. в столешницах, стеновых плитах и др.)&nbsp;- регулярная работа для компаний, которые специализируются в резке природного или искусственного камня. Обычно отверстия необходимы, чтобы установить сантехническое, электро- оборудование или специальные лючки для проникновение за облицовку. При этом ставится задача сохранить целостность плиты из эстетических соображений. В случае использования гидроабразивной резки - не имеет значение какова форма отверстия и даже толщина материала, это вполне реализуемо.</p>
INSERT INTO product (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, article, cost, image, id_surface, id_pattern, id_country, is_action, is_new, is_hit, width, height, depth, image_preview, id_collection) VALUES (124, true, 'Гавань 42*42', '<p>Керамогранит Гавань - керамогранит с глазурованной поверхностью. Тема рисунка - обработанный камень.&nbsp;Поскольку этот матовый керамогранит изготавливается&nbsp;в формате 420*420, он неплохо подходит для отделки частных интерьеров, ресторанов и других помещений с небольшими пространствами.</p>
