INSERT INTO rek (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, params) VALUES (104, true, 'Керамогранит под металл', '<p>Керамогранит под металл среди российских&nbsp;фабрик производит только завод Керама-Марацци.&nbsp;&quot;Металлическая&quot; плитка -&nbsp;весьма редкая имитация,&nbsp;фигурирует в основном в &quot;крутом&quot; дизайне, где внешний вид превалирует над всем остальным.&nbsp;Это то, что в народе говорят - гламурная вещь.</p>
