INSERT INTO rek (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, params) VALUES (90, true, 'Полированный керамогранит', '<p>Полированный керамогранит очень популярен в России, и практически неизвестен в Западной Европе, почему? Причина в разности менталитета Запада и Востока. Глянцевая поверхность керамогранита, с зеркальной поверхностью &mdash; это показатель богатства, роскоши. С точки зрения практичности, глянцевая плитка, возможно, проигрывает неполированной, т. к. в помещениях с большой проходимостью быстро потеряет свой блеск, а на фасадах слишком сильно бликует.</p>
