INSERT INTO rek (id, is_published, title, content, page_url, page_title, page_description, page_keywords, "order", name, is_locked, id_parent, path, entity, created_at, updated_at, params) VALUES (103, true, 'Керамогранит под дерево', '<p>Практичность камня и&nbsp;уют натурального материала способен совместить в&nbsp;себе лишь керамогранит под дерево. Многообразие природных фактур древесных пород в&nbsp;своих коллекциях стремятся передать все известные производители керамогранита. Сегодня качественный уровень создания керамогранита достиг того уровня, когда потребитель получает практически полную имитацию текстуры и&nbsp;цвета различных пород древесины.</p>
